import React, { useState } from "react";

const Auth = () => {

  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [email,setEmail] = useState(null);
  const [password,setPassword] = useState(null);
  const [confirmPassword,setConfirmPassword]= useState(null);

  const viewLogin = (status) => {
    setError(null);
    setIsLoggedIn(status);
  };

  const handleSubmit = async(e,endpoint)=>{
    e.preventDefault();
    if(!isLoggedIn && password !== confirmPassword){
      setError("Make sure passwords match!") 
      return
    }

    await fetch(`${process.env.REACT_APP_SERVERURL}/${endpoint}`)
  }

  return (
    <div className="auth-container">
      <div className="auth-container-box">
        <form>
          <h2>{isLoggedIn ? "Please log in" : "Please sign up!"}</h2>
          <input type="email" placeholder="email" />
          <input type="password" placeholder="password" />
          {!isLoggedIn && (
            <input type="password" placeholder="confirm password" />
          )}
          <input type="submit" className="create-user" onClick={()=>handleSubmit(e, isLoggedIn ? 'login' : 'signup')} />
          {error && <p>{error}</p>}
        </form>
        <div className="auth-options">
          <button
            onClick={() => viewLogin(false)}
            style={{
              backgroundColor: !isLoggedIn ? "#f2f2f2" : "rgb(188,188,188)",
            }}
          >
            Sign Up
          </button>
          <button
            onClick={() => viewLogin(true)}
            style={{
              backgroundColor: isLoggedIn ? "#f2f2f2" : "rgb(188,188,188)",
            }}
          >
            Login
          </button>
        </div>
      </div>
    </div>
  );
};

export default Auth;
