import React, { useState } from "react";

const CourseModal = ({ mode,setShowPopup, getCourses, courseInfo ,setMode, setCourseInfo}) => {
    const editMode = mode === "edit" 
    console.log(mode);
  const [course, setCourse] = useState({
    courseid: editMode ? courseInfo.id : "",
    course_name: editMode ? courseInfo.name : "",
  });

  const postCourse = async () => {
    try {
      const response = await fetch("http://localhost:5000/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(course),
      });
      if (response.status === 200) {
        console.log("postCourse çalıştı");
        getCourses();
        setShowPopup(false);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const updateCourse = async(id)=>{
    try {
        const response = await fetch(`http://localhost:5000/courses/${id}`,{
            method: 'PUT',
            headers: {"Content-Type" : "application/json"},
            body: JSON.stringify(courseInfo),
        })
        if(response.status === 200){
        console.log("updateCourse çalıştı");
            getCourses();
            setShowPopup(false);
        }
    } catch (error) {
        console.log(error)
    }
  }

  const handleId = (e) => {
    if(editMode)setCourse((prevState) => ({
      ...prevState,
      courseid: e.target.value,
    }));
    else setCourseInfo
  };
  const handleName = (e) => {
    setCourse((prevState) => ({
      ...prevState,
      course_name: e.target.value,
    }));
  };
  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
          {" "}
          <div className="col-lg-10">
            <h2 className="popup-title">Add New Course</h2>
          </div>
          <div className="col-lg-2">
            <button
              onClick={() => {setShowPopup(false); setMode('create');}}
              className="close-button"
            >
              <i className="fa-sharp fa-solid fa-circle-xmark"></i>
            </button>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12">
            <label for="course-id">Course ID</label>
          </div>
          <div className="col-lg-12">
            <input
              type="text"
              id="course-id"
              maxLength={5}
              onChange={handleId}
              required
              value={editMode ? courseInfo.id : course.courseid}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12">
            <label for="course-name">Course Name</label>
          </div>
          <div className="col-lg-12">
            <input
              type="text"
              id="course-name"
              maxLength={130}
              onChange={handleName}
              required
              value={editMode ? courseInfo.name : course.course_name}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-lg-4"></div>
          <div className="col-lg-4 d-flex justify-content-center">
            <button className="save-button" onClick={editMode ? updateCourse:postCourse}>
              Save
            </button>
          </div>
          <div className="col-lg-4"></div>
        </div>
      </div>
    </div>
  );
};

export default CourseModal;
