import React, { useState } from "react";
import Spinner from "../global/Spinner";
import AddNewCourseToStudent from "../course/AddNewCourseToStudent";

const ShowPopup = ({
  studentId,
  firstname,
  lastname,
  city,
  dob,
  phone,
  courses,
  setShowPopup,
  isCourseFetched,
  allCourses,
  getStudentCourses,
}) => {
  const handleChange = (e) => {
    console.log(e.target.value);
  };

  const [showCourse, setShowCourse] = useState(false);

  const banishCourse = async ()=>{
    console.log("I banish you!")
    try {
      const response = await fetch('http://localhost:3000/students/courses/',{
        method: 'DELETE',
        headers: {"Content-Type" : "application/json"},
        body: JSON.stringify(data),
      });
      if(response.status === 200){
  getStudentCourses();
      }
      
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
          <div className="col-lg-10">
            <h2 className="popup-title">Student Card</h2>
          </div>
          <div className="col-lg-2">
            <button
              className="close-button"
              onClick={() => setShowPopup(false)}
            >
              <i className="fa-sharp fa-solid fa-circle-xmark"></i>
            </button>
          </div>
        </div>
        <div className="row">
          <form className="row-form">
            <div className="col-lg-4">
              <label>Student ID</label>
              <input
                type="text"
                placeholder="Student ID..."
                required
                maxLength={10}
                name="studentId"
                value={studentId}
                onChange={handleChange}
                disabled
              />
            </div>
            <div className="col-lg-4">
              <label>First Name</label>
              <input
                type="text"
                placeholder="First name..."
                required
                maxLength={30}
                name="firstname"
                value={firstname}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4">
              <label>Last Name</label>
              <input
                type="text"
                placeholder="Last Name..."
                required
                maxLength={30}
                name="lastname"
                value={lastname}
                onChange={handleChange}
              />
            </div>
          </form>
        </div>

        <div className="row">
          <form className="row-form">
            <div className="col-lg-4">
              {" "}
              <label>City</label>
              <input
                type="text"
                placeholder="City..."
                required
                maxLength={30}
                name="city"
                value={city}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4">
              {" "}
              <label>Date of Birth</label>
              <input
                type="text"
                placeholder="Date of Birth..."
                required
                maxLength={30}
                name="dob"
                value={dob}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4">
              {" "}
              <label>Phone Number</label>
              <input
                type="text"
                placeholder="Phone Number..."
                required
                maxLength={16}
                name="number"
                value={phone}
                onChange={handleChange}
              />
            </div>
          </form>
        </div>
        <div className="row">
          <div className="col-lg-12 d-flex justify-content-between ">
            <p className="pt-2">Enrolled courses </p>{" "}
            <button className="new-course " onClick={() => setShowCourse(true)}>
              <i className="fa-solid fa-plus"></i> New Course
            </button>
          </div>
        </div>
        <div className="overflow">
        {!isCourseFetched ? (
          courses.map((item, index) => (
            <div className="row" key={index}>
              <div className="col-lg-4 course">{item.courseid}</div>
              <div className="col-lg-4">{item.course_name}</div>
              <div className="col-lg-4">
              <button className="" onClick={banishCourse}>
                  <i className="fa-solid fa-trash"></i>
                </button>
              </div>
             
            </div>
          ))
        ) : (
          <Spinner />
        )}
        </div>
       
        {showCourse && (
          <AddNewCourseToStudent
            allCourses={allCourses}
            getStudentCourses={getStudentCourses}
            studentId={studentId}
            setShowCourse={setShowCourse}
          />
        )}
      </div>
    </div>
  );
};

export default ShowPopup;
