import React, { useState } from "react";

const Auth = () => {
  const viewLogin = (status) => {
    setError(null);
    setIsLoggedIn(status);
  };

  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  return (
    <div className="container auth-container">
      <div className="row">
        <form className="col-lg-12 col-md-12 col-xs-12">
          <h2>{isLoggedIn ? "Please log in" : "Please sign up!"}</h2>
          <input type="email" placeholder="email" />
          <input type="password" placeholder="password" />
          {isLoggedIn && (
            <input type="password" placeholder="confirm password" />
          )}
          <input type="submit" className="create-user" />
          {error && <p>{error}</p>}
        </form>
      </div>
      <div className="row auth-options">
        <button className="col-lg-2 btn"
          onClick={() => viewLogin(false)}
          style={{ backgroundColor: !isLoggedIn ? "#f2f2f2" : "rgb(188,188,188)" }}
        >
          Sign Up
        </button>
        <button
          onClick={() => viewLogin(true)}
          style={{ backgroundColor: isLoggedIn ? "#f2f2f2" : "rgb(188,188,188)" }}
        >
          Login
        </button>
      </div>
    </div>
  );
};

export default Auth;
