import React, { useEffect, useState, useCallback } from "react";
import Spinner from "../global/Spinner";
import Signout from "../global/Signout";

export const StudentPage = ({
  studentid,
  setUserMode,
  setAuthToken,
  setStudentId,
}) => {
  const [data, setData] = useState({});
  const [course, setCourse] = useState(null);
  const [isCourseFetched, setIsCourseFetched] = useState(false);
  const [isDataFetched, setIsDataFetched] = useState(false);

  const getStudent = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVERURL}/students/${studentid}`
      );
      const json = await response.json();
      if (response.status === 200) {
        setData(json);
        setIsDataFetched(true);
      }
    } catch (error) {
      console.log(error.detail);
    }
  };

  const getStudentCourses = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVERURL}/students/enrolled/${studentid}`
      );
      const json = await response.json();
      if (response.status === 200) {
        setCourse(json);
        setIsCourseFetched(true);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const deleteCourse = async (courseid) => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVERURL}/students/${data.studentid}/courses/${courseid}`,
        {
          method: "DELETE",
        }
      );
      console.log(response);

      if (response.status === 200) {
        console.log("deleted the course");
        getStudentCourses();
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleChange = useCallback(
    (e) => {
      const { name, value } = e.target;

      setData((data) => ({
        ...data,
        [name]: value,
      }));
    },

    [setData]
  );

  useEffect(() => {
    getStudent();
    getStudentCourses();
  }, []);

  return (
    <>
      {isDataFetched ? (
        <div className="container w-50 bg-light student-page-container">
          <div className="row ">
            <div className="col-lg-6 col-md-6 col-xs-6 d-flex justify-content-center align-content-center">
              {" "}
              <p className="pt-2 student-page-welcome">
                Welcome {data.firstname + " " + data.lastname}
              </p>
            </div>
            <div className="col-lg-2"></div>
            <div className="col-lg-4  d-flex justify-content-center">
              <Signout
                setUserMode={setUserMode}
                setAuthToken={setAuthToken}
                setStudentId={setStudentId}
              />
            </div>
          </div>
          <div className="row w-100">
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>First Name</label>
              <input
                type="text"
                defaultValue={data.firstname}
                onChange={(e) => handleChange(e)}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Last Name</label>
              <input
                type="text"
                defaultValue={data.lastname}
                onChange={(e) => handleChange(e)}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column ">
              <label>City</label>
              <input
                type="text"
                defaultValue={data.city}
                onChange={(e) => handleChange(e)}
              />
            </div>
          </div>
          <div className="row w-100">
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Birthdate</label>
              <input
                type="text"
                defaultValue={data.dateofbirth}
                onChange={(e) => handleChange(e)}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Phone</label>
              <input
                type="text"
                defaultValue={data.phone}
                maxLength={10}
                onChange={(e) => handleChange(e)}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Email</label>
              <input
                type="text"
                defaultValue={data.email}
                onChange={(e) => handleChange(e)}
              />
            </div>
          </div>

          <div className="row">
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Password</label>
              <input type="password" onChange={(e) => handleChange(e)} />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
              <label>Confirm Password</label>
              <input type="password" onChange={(e) => handleChange(e)} />
            </div>
            <div className="col-lg-2 col-md-2 col-xs-2 d-flex  align-self-center ">
              <p className="error">Error</p>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-3">Course ID</div>
            <div className="col-lg-6">Course Name</div>
            <div className="col-lg-3">+ New Course</div>
          </div>
          <div className="overflow-in-student-page">
            {Array.isArray(course) && isCourseFetched ? (
              course.map((item) => (
                <div className="row">
                  <div className="col-lg-3">{item.courseid}</div>
                  <div className="col-lg-6">{item.course_name}</div>
                  <div className="col-lg-3">
                    <button onClick={() => deleteCourse(item.courseid)}>
                      <i className="fa-solid fa-trash"></i>
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <Spinner />
            )}
            <div className="row">
              <div className="col-lg-3"></div>
              <div className="col-lg-6"><button className="save"></button> </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        </div>
      ) : (
        <Spinner />
      )}
    </>
  );
};
