import React, { useState } from "react";

const AddNewCourseToStudent = ({ studentId, setShowCourse, allCourses }) => {
  const [data, setData] = useState({
    studentid: studentId,
    courseid: "",
  });

  const handleChange = (e) => {
    setData((prevState) => ({
      ...prevState,
      courseid: e.target.value,
    }));
  };

  const addCourse = async () => {
    try {
      const response = await fetch("http://localhost:5000/students/",{
        method: 'POST',
        headers: {"Content-Type":"application/json"},
        body: JSON.stringify(data)
      });
      if(response.status === 200){
        setShowCourse(false);
      }
    } catch (error) {}
  };

  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
          <div className="col-lg-10">
            <h2 className="popup-title">Course Card</h2>
          </div>
          <div className="col-lg-2">
            <button
              className="close-button"
              onClick={() => setShowCourse(false)}
            >
              <i className="fa-sharp fa-solid fa-circle-xmark"></i>
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-lg-4 col-md-4 col-xs-4">
            <label for="courseid">Course ID</label>
            <input
              type="text"
              placeholder="Enter Course ID..."
              maxLength={5}
              required
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-8 col-md-8 col-xs-8 all-courses-list h-25">
            Available Courses
            {allCourses.map((item, index) => (
              <div key={index} className="row bg-white">
                <div className="col-lg-4">{item.courseid}</div>
                <div className="col-lg-8">{item.course_name}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddNewCourseToStudent;
