import React, { useState } from "react";
import ShowPopup from "../global/ShowPopup";

const ListItems = ({
  studentId,
  firstname,
  lastname,
  key,
  city,
  dob,
  phone,
}) => {
  const [showPopup, setShowPopup] = useState(false);

  return (
    <div className="list-students-body">
      <div className="list-students-item" key={key}>
        <li>{studentId}</li>
        <li>{firstname}</li>
        <li>{lastname}</li>
      </div>
      <div className="list-button-container">
        <button onClick={() => setShowPopup(true)}>
          <i class="fa-regular fa-pen-to-square"></i>
        </button>
        <button>
          <i class="fa-solid fa-trash"></i>
        </button>
      </div>
      {showPopup && (
        <ShowPopup
          studentId={studentId}
          firstname={firstname}
          lastname={lastname}
          city={city}
          dob={dob}
          phone={phone}
          setShowPopup={setShowPopup}
        />
      )}
    </div>
  );
};

export default ListItems;
