import React, { useState } from "react";
import Spinner from "../global/Spinner";
import AddNewCourseToStudent from "../course/AddNewCourseToStudent";

const StudentModal = ({
  studentId,
  firstname,
  lastname,
  city,
  dob,
  phone,
  email,
  respectiveCourses,
  setShowPopup,
  isCourseFetched,
  allCourses,
  getStudentCourses,
}) => {
  const handleChange = (e) => {
    console.log(e.target.value);
  };

  const [showCourse, setShowCourse] = useState(false);

  const banishCourse = async (courseid, studentid) => {
    console.log("I banish you!");
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER}/students/${studentid}/courses/${courseid}`,
        {
          method: "DELETE",
        }
      );
      if (response.status === 200) {
        getStudentCourses();
      }
    } catch (error) {
      console.log(error.detail);
    }
  };

  return (
    <div className="overlay">
      <div className=" popup">
        <div className="row">
          <div className="col-lg-10 col-md-10 col-xs-10">
            <h2 className="popup-title">Student Card</h2>
          </div>
          <div className="col-lg-2 col-md-2 col-xs-2">
            <button
              className="close-button"
              onClick={() => setShowPopup(false)}
            >
              <i className="fa-sharp fa-solid fa-circle-xmark"></i>
            </button>
          </div>
        </div>
        {/* <div className="row">
            <div className="col-lg-4 col-md-4 col-xs-4">
              <label>Student ID</label>
              <input
                type="text"
                placeholder="Student ID..."
                required
                maxLength={10}
                name="studentId"
                value={studentId}
                onChange={handleChange}
                disabled
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4">
              <label>First Name</label>
              <input
                type="text"
                placeholder="First name..."
                required
                maxLength={30}
                name="firstname"
                value={firstname}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4">
              <label>Last Name</label>
              <input
                type="text"
                placeholder="Last Name..."
                required
                maxLength={30}
                name="lastname"
                value={lastname}
                onChange={handleChange}
              />
            </div>
        </div>

        <div className="row">
            <div className="col-lg-4 col-md-4 col-xs-4">
              {" "}
              <label>City</label>
              <input
                type="text"
                placeholder="City..."
                required
                maxLength={30}
                name="city"
                value={city}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4">
              {" "}
              <label>Date of Birth</label>
              <input
                type="text"
                placeholder="Date of Birth..."
                required
                maxLength={30}
                name="dob"
                value={dob}
                onChange={handleChange}
              />
            </div>
            <div className="col-lg-4 col-md-4 col-xs-4">
              {" "}
              <label>Phone Number</label>
              <input
                type="text"
                placeholder="Phone Number..."
                required
                maxLength={16}
                name="number"
                value={phone}
                onChange={handleChange}
              />
            </div>
        </div> * */}

        <div className="row w-100">
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
            <label>First Name</label>
            <input
              type="text"
              placeholder="First name..."
              required
              maxLength={30}
              name="firstname"
              value={firstname}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
            <label>Last Name</label>
            <input
              type="text"
              placeholder="Last Name..."
              required
              maxLength={30}
              name="lastname"
              value={lastname}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column ">
            <label>City</label>
            <input
              type="text"
              placeholder="City..."
              required
              maxLength={30}
              name="city"
              value={city}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row w-100">
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
            <label>Birthdate</label>
            <input
              type="text"
              placeholder="Date of Birth..."
              required
              maxLength={30}
              name="dob"
              value={dob}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
            <label>Phone</label>
            <input
              type="text"
              placeholder="Phone Number..."
              required
              maxLength={10}
              name="number"
              value={phone}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4 col-md-4 col-xs-4 d-flex justfify-content-center flex-column">
            <label>Email</label>
            <input
              type="email"
              placeholder="Phone Number..."
              required
              maxLength={10}
              name="number"
              value={email}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12 col-md-12 col-xs-12 d-flex justify-content-between ">
            <p className="pt-2">Enrolled courses </p>{" "}
            <button className="new-course" onClick={() => setShowCourse(true)}>
              <i className="fa-solid fa-plus"></i> New Course
            </button>
          </div>
        </div>
        <div className="overflow">
          {Array.isArray(respectiveCourses) ? (
            respectiveCourses.map((item, index) => (
              <div className="row" key={index}>
                <div className="col-lg-4 col-lg-4 col-md-4 col-xs-4course">
                  {item.courseid}
                </div>
                <div className="col-lg-4 col-lg-4 col-md-4 col-xs-4">
                  {item.course_name}
                </div>
                <div className="col-lg-4 col-lg-4 col-md-4 col-xs-4">
                  <button
                    className=""
                    onClick={() => banishCourse(item.courseid, studentId)}
                  >
                    <i className="fa-solid fa-trash"></i>
                  </button>
                </div>
              </div>
            ))
          ) : (
            <Spinner />
          )}
        </div>
        <div className="row">
          <div className="col-lg-3"></div>
          <div className="col-lg-6 d-flex justify-content-center">
            <button className="new-course pr-5 pl-5 pt-2">Save</button>
          </div>
          <div className="col-lg-3"></div>
        </div>

        {showCourse && (
          <AddNewCourseToStudent
            allCourses={allCourses}
            getStudentCourses={getStudentCourses}
            studentId={studentId}
            setShowCourse={setShowCourse}
          />
        )}
      </div>
    </div>
  );
};

export default StudentModal;
