import React, { useEffect, useState } from "react";
import ListStudents from "./ListStudents";
import ListCourses from "./ListCourses";
import Spinner from "../global/Spinner";

const AdminPage = () => {
  const [showPage, setShowPage] = useState("students");
  const [students, setStudents] = useState(null);
  const [isDataFetched, setIsDataFetched] = useState(false);
  
  const handleList = (e) => {
    setShowPage(e.target.innerText.toLowerCase());
  };



  //This will get all the students from database.

  const getData = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/students`);
      const json = await response.json();
      if (response.status === 200) {
        setStudents(json);
        setIsDataFetched(true);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  console.log(students);
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-12 w-25 d-flex">
          <button onClick={(e) => handleList(e)}>Students</button>
          <button onClick={(e) => handleList(e)}>Courses</button>
        </div>
      </div>
      <div className="admin-page-list">
        {isDataFetched ? (
          showPage === "students" ? (
            <ListStudents
              students={students}
              setStudents={setStudents}
              getStudents={getData}
            />
          ) : (
            <ListCourses students={students} />
          )
        ) : (
          <Spinner />
        )}
      </div>
    </div>
  );
};

export default AdminPage;
