import React, { useEffect, useState } from "react";
import SearchBar from "../global/SearchBar";
import ListItems from "./ListItems";
import AddNewStudentModal from "../student/AddNewStudentModal";

const ListStudents = ({ students,comeFromCourse,getStudents,setStudents }) => {
  const [searchText, setSearchText] = useState("");
  const [showPopup,setShowPopup] = useState(false);


  
  const deleteStudent = async(studentid)=>{
    try {
      const response = await fetch(`http://localhost:5000/students/${studentid}`,{
        method:'DELETE',
      })
      if(response.status===200)
      //Filter out the deleted courses
      setStudents(students.filter(student => student.studentId !== studentid));
      getStudents();
    } catch (error) {
      console.log(error.detail)
    }
  }

  return (
    <>
    <div className="row">
        <div className="col-lg-12 d-flex justify-content-between align-self-center">
          <p className="align-self">Enrolled Students</p>
          <button onClick={()=>setShowPopup(true)} className="btn"><i className="fa-solid fa-plus"></i> New Student</button>
        </div>
    </div>
        {students.map((item, index) => (
          <ListItems
            key={index}
            studentId={item.studentid}
            firstname={item.firstname}
            lastname={item.lastname}
            city={item.city}
            dob={item.dateofbirth}
            phone={item.phone}
            comeFromCourse={comeFromCourse}
            getStudents={getStudents}
            deleteStudent={deleteStudent}
          />
        ))}
        {showPopup && <AddNewStudentModal getStudents={getStudents}
          setShowPopup={setShowPopup}
        />} 
  </>
  );

};

export default ListStudents;
