import { useState } from "react";
import AdminPage from "./components/admin/AdminPage";
import { StudentPage } from "./components/student/StudentPage";
import Auth from "./components/global/Auth";
import Signout from "./components/global/Signout";

function App() {
  const [userMode, setUserMode] = useState("");
  const [studentId,setStudentId] = useState("");
  const [authToken, setAuthToken] = useState(true);

  console.log(userMode,studentId, "in app.js")
  return (
    <div className="main-container">
    {authToken && <Signout setUserMode={setUserMode} setAuthToken={setAuthToken} setStudentId={setStudentId} />}
      {!authToken ? (
        <Auth setUserMode={setUserMode} setAuthToken={setAuthToken} setStudentId={setStudentId}/>
      ) : userMode !== "admin" ? (
        <StudentPage studentid={studentId}/>
      ) : (
        <AdminPage />
      )}

    </div>
  );
}

export default App;
