import React, { useEffect, useState } from "react";
import StudentModal from "../modals/StudentModal";
import ListItems from "../admin/ListItems";
import Spinner from "../global/Spinner";

export const StudentPage = ({ studentid }) => {
  const [data, setData] = useState({});
  const [isDataFetched, setIsDataFetched] = useState(false);

  const getStudent = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVERURL}/students/${studentid}`
      );
      const json = await response.json();
      if (response.status === 200) {
        setData(json);
        setIsDataFetched(true);
      }
    } catch (error) {
      console.log(error.detail);
    }
  };

  // studentId={data.studentid}
  // firstname={data.firstname}
  // lastname={data.lastname}
  // city={data.city}
  // dob={data.dateofbirth}
  // phone={data.phone}
  // email={data.email}

  useEffect(() => {
    getStudent();
  }, []);

  return (
    <>
      {isDataFetched ? (
        <div className="container w-50">
          <div className="row">
            <div className="col-lg-12">
              Welcome {data.firstname + " " + data.lastname}
            </div>
          </div>
          <div className="row ">
            <div className="col-lg-4">
              <label>Student ID</label>
              <input type="text" value={data.studentid} />
            </div>
            <div className="col-lg-4">
              <label>First Name</label>
              <input type="text" value={data.firstname} />
            </div>
            <div className="col-lg-4 mr-2">
              <label>Last Name</label>
              <input type="text" value={data.lastname} />
            </div>
          </div>
          <div className="row">
            {" "}
            <div className="col-lg-4">
              <label>Student ID</label>
              <p>{data.studentid}</p>
            </div>
          </div>
        </div>
      ) : (
        <Spinner />
      )}
    </>
  );
};
