import React, { useEffect, useState } from "react";
import SearchBar from "../global/SearchBar";
import ListItems from "./ListItems";
import AddNewStudentModal from "../student/AddNewStudentModal";
import Spinner from "../global/Spinner";

const ListStudents = () => {
  const [searchText, setSearchText] = useState("");
  const [showPopup,setShowPopup] = useState(false);
  const [students,setStudents] = useState(null);
  const [isDataFetched, setIsDataFetched] = useState(false);

//This will get all the students from database.

const getData = async () => {
  try {
    const response = await fetch(`http://localhost:5000/students`);
    const json = await response.json();
    setStudents(json);
    setIsDataFetched(true);
  } catch (error) {
    console.error(error);
  }
};


useEffect(() => {getData()}, []);

  return (
    <div className="list-students">
      <SearchBar setSearchText={setSearchText} />
      <div className="list-students-context">
        <div className="list-students-header">
          <p>Enrolled Students</p>
          {/* <button onClick={()=>setShowPopup(true)}><i className="fa-solid fa-plus"></i> New Student</button> */}
        </div>
        {!isDataFetched ? students.map((item, index) => (
          <ListItems
            key={index}
            studentId={item.studentid}
            firstname={item.firstname}
            lastname={item.lastname}
            city={item.city}
            dob={item.dateofbirth}
            phone={item.phone}
          />
        )): <Spinner />}
        {/* {showPopup && <AddNewStudentModal 
          setShowPopup={setShowPopup}
        />} */}
      </div>
    </div>
  );
};

export default ListStudents;
