import React, { useState } from "react";

const CourseModal = ({ setShowPopup }) => {

    const [course,setCourse] = useState({
        courseid:'',
        course_name:'',
    });

const handleChange = (e) => {
    console.log(e.target);
}

  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
          {" "}
          <div className="col-lg-10">
            <h2 className="popup-title">Add New Course</h2>
          </div>
          <div className="col-lg-2">
            <button onClick={() => setShowPopup(false)} className="close-button"><i className="fa-sharp fa-solid fa-circle-xmark"></i></button>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12">
            <label for="course-id">Course ID</label>
          </div>
          <div className="col-lg-12">
            <input type="text" id="course-id" maxLength={5} onChange={handleChange}/>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12">
            <label for="course-name">Course Name</label>
          </div>
          <div className="col-lg-12">
            <input type="text" id="course-name" maxLength={130} onChange={handleChange}/>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-4"></div>
          <div className="col-lg-4 d-flex justify-content-center">
            <button className="save-button">Save</button>
          </div>
          <div className="col-lg-4"></div>
        </div>
      </div>
    </div>
  );
};

export default CourseModal;
