import React from "react";

const ShowPopup = ({studentId,firstname,lastname,city,dob,phone}) => {

    const handleChange = (e)=>{
        console.log(e.target.value);
    }

  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
            <div className="col-lg-4"><label>Student ID</label>
            <input type="text" placeholder="Student ID..." required maxLength={10} name="studentId" value={firstname} onChange={handleChange} />
            </div>
            <div className="col-lg-4">firstname</div>
            <div className="col-lg-4">lastname</div>
        </div>
        <div className="row">
        <div className="col-lg-4">city</div>
            <div className="col-lg-4">dob</div>
            <div className="col-lg-4">phone</div>
        </div>
        <div className="row">
            <div className="col-lg-12">Enrolled courses</div>
        </div>
        <div className="row">
        <div className="col-lg-4">city</div>
        <div className="col-lg-4">dob</div>
        <div className="col-lg-2"><button className="btn ">edit</button></div>
        <div className="col-lg-2"><button className="btn">delete</button></div>
        </div>
      </div>
    </div>
  );
};

export default ShowPopup;
