import React from "react";

const ShowPopup = ({ studentId, firstname, lastname, city, dob, phone }) => {
  const handleChange = (e) => {
    console.log(e.target.value);
  };

  return (
    <div className="overlay">
      <div className="popup">
        <div className="row">
          <form className="row-form">
          <div className="col-lg-4">
            <label>Student ID</label>
            <input
              type="text"
              placeholder="Student ID..."
              required
              maxLength={10}
              name="studentId"
              value={studentId}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4">
            <label>First Name</label>
            <input
              type="text"
              placeholder="First name..."
              required
              maxLength={30}
              name="firstname"
              value={firstname}
              onChange={handleChange}
            />
          </div>
          <div className="col-lg-4">
            <label>Last Name</label>
            <input
              type="text"
              placeholder="Last Name..."
              required
              maxLength={30}
              name="lastname"
              value={lastname}
              onChange={handleChange}
            />
          </div>
          </form>
        </div>
        <div className="row">
        <form className="row-form">
          <div className="col-lg-4"> <label>City</label>
            <input
              type="text"
              placeholder="City..."
              required
              maxLength={30}
              name="city"
              value={city}
              onChange={handleChange}
            /></div>
          <div className="col-lg-4"> <label>Date of Birth</label>
            <input
              type="text"
              placeholder="Date of Birth..."
              required
              maxLength={30}
              name="dob"
              value={dob}
              onChange={handleChange}
            /></div>
          <div className="col-lg-4"> <label>Phone Number</label>
            <input
              type="text"
              placeholder="Phone Number..."
              required
              maxLength={16}
              name="number"
              value={phone}
              onChange={handleChange}
            /></div>
            </form>
        </div>
        <div className="row">
          <div className="col-lg-12">Enrolled courses</div>
        </div>
        <div className="row">
          <div className="col-lg-4">city</div>
          <div className="col-lg-4">dob</div>
          <div className="col-lg-2">
            <button className="btn ">edit</button>
          </div>
          <div className="col-lg-2">
            <button className="btn">delete</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowPopup;
