import React, { useEffect, useState } from "react";
import StudentModal from "../modals/StudentModal";

const ListItems = ({
  studentId,
  firstname,
  lastname,
  key,
  city,
  dob,
  phone,
  comeFromCourse
}) => {
  const [showPopup, setShowPopup] = useState(false);
  const [courses,setCourses] = useState(null)
  const [allCourses,setAllCourses] = useState(null);
  const [isCourseFethced, setIsCourseFetched] = useState(false);


   const getStudentCourses = async ()=>{
    try {
      const response = await fetch(`http://localhost:5000/students/${studentId}`)
      const json = await response.json();
      setCourses(json);
      setIsCourseFetched(true);
    } catch (error) {
      console.log(error);
    }
  }

  const getCourses = async () => {
    try {
      const response = await fetch("http://localhost:5000/courses/");
      const json = await response.json();
      setAllCourses(json);
    } catch (error) {
      console.error(error);
    }
  };

useEffect(() => {getStudentCourses();getCourses()}, [])


  return (
    <div className="list-students-body">
      <div className="list-students-item row" key={key}>
        <li>{studentId}</li>
        <li>{firstname}</li>
        <li>{lastname}</li>
        <li>{courses.map((item)=>item.courseid+",")}</li>
      </div>
      {comeFromCourse ? "": <div className="list-button-container">
        <button onClick={() => setShowPopup(true)}>
          <i className="fa-regular fa-pen-to-square"></i>
        </button>
        <button>
          <i className="fa-solid fa-trash"></i>
        </button>
      </div>}
      
      {showPopup && (
        <StudentModal
          studentId={studentId}
          firstname={firstname}
          lastname={lastname}
          city={city}
          dob={dob}
          phone={phone}
          courses={courses}
          setShowPopup={setShowPopup}
          isCourseFethced={isCourseFethced}
          allCourses={allCourses}
          getStudentCourses={getStudentCourses}
        />
      )}
    </div>
  );
};

export default ListItems;
