import React, { useEffect, useState } from "react";
import StudentModal from "../modals/StudentModal";

const ListItems = ({
  studentId,
  firstname,
  lastname,
  key,
  city,
  dob,
  phone,
  comeFromCourse,
  getStudents,
  deleteStudent
}) => {
  const [showPopup, setShowPopup] = useState(false);
  const [courses, setCourses] = useState([]);
  const [allCourses, setAllCourses] = useState(null);
  const [isCourseFetched, setIsCourseFetched] = useState(false);

  // this will get all courses for other section
  const getCourses = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/courses/`);
      const json = await response.json();
      setAllCourses(json);
    } catch (error) {
      console.error(error);
    }
  };

  //this will get courses by studentid. Means that every student will see his/her courses
  const getStudentCourses = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVERURL}/students/${studentId}`
      );
      const json = await response.json();
      setCourses(json);
      setIsCourseFetched(true);
    } catch (error) {
      console.log(error);
    }
  };


  useEffect(() => {
    getStudentCourses();
    getCourses();
  }, [studentId]);


  const handleDeleteStudent = async(studentId)=>{
    try {
       // Make API call to delete the student
       await deleteStudent(studentId);

       // Update the student courses in local state (after deleting the student)
       setCourses(courses.filter(item => item.studentId !== studentId));
 
     } catch (error) {
       console.log(error);
     }
   }

  return (
    <div className="row d-flex justify-content-center align-items-center list-items-row">
      <div className="col-lg-2 col-md-2 col-xs-2 list-items-col ">{studentId}</div>
      <div className="col-lg-2 col-md-2 col-xs-2 list-items-col ">{firstname}</div>
      <div className="col-lg-2 col-md-2 col-xs-2 list-items-col ">{lastname}</div>
      {Array.isArray(courses) && isCourseFetched ? (
        <div className="col-lg-4 col-md-4 col-xs-4 overflow-in-span">
          {courses.map((item) => item.courseid+" ")}
        </div>
      ) : (
        "deneme"
      )}

      {comeFromCourse ? (
        ""
      ) : (
        <div className="col-lg-2 col-md-2 col-xs-2 list-items-col">
          <button onClick={() => setShowPopup(true)} className="mr-1">
            <i className="fa-regular fa-pen-to-square"></i>
          </button>
          <button onClick={()=>handleDeleteStudent(studentId)}>
            <i className="fa-solid fa-trash"></i>
          </button>
        </div>
      )}

      {showPopup && (
        <StudentModal
          studentId={studentId}
          firstname={firstname}
          lastname={lastname}
          city={city}
          dob={dob}
          phone={phone}
          courses={courses}
          setShowPopup={setShowPopup}
          isCourseFethced={isCourseFetched}
          allCourses={allCourses}
          getStudentCourses={getStudentCourses}
        />
      )}
    </div>
  );
};

export default ListItems;
