import React, { useEffect, useState } from "react";
import Spinner from "../global/Spinner";
import CourseModal from "../modals/CourseModal";

const ListCourses = () => {
  const [isDataFetched, setIsDataFetched] = useState(false);
  const [courses, setCourses] = useState(null);
  const [showPopup, setShowPopup] = useState();
  const [mode, setMode] = useState("edit");

  const getCourses = async () => {
    try {
      const response = await fetch("http://localhost:5000/courses/");
      const json = await response.json();
      setCourses(json);
      setIsDataFetched(true);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteCourse = async (courseid) => {
    console.log(courseid);
    try {
      const response = await fetch(
        `http://localhost:5000/courses/${courseid}`,
        {
          method: "DELETE",
        }
      );
      console.log(response);

      if (response.status === 200) {
        console.log("deleted the course");
        getCourses();
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleModal = (mode) => {
    setShowPopup(true);
    setMode(mode);

  };

  useEffect(() => {
    getCourses();
  }, []);

  

  return (
    <div className="container course">
      <div className="row course-title">
        <div className="col-lg-4 title-col ">
          <p>Course Id</p>
        </div>
        <div className="col-lg-4 title-col">
          <p>Course name</p>
        </div>
        <div className="col-lg-4 title-col">
          <button onClick={() => handleModal("create")}>
            <i className="fa-solid fa-plus"></i>New Course
          </button>
        </div>
      </div>
      {isDataFetched ? (
        courses.map((item, index) => (
          <div className="row course-row">
            <div className="col-lg-4 ">{item.courseid}</div>
            <div className="col-lg-4 ">{item.course_name}</div>
            <div className="col-lg-2">
              <button className="edit" onClick={() => handleModal("edit",item.courseid,item.course_name)}>
                <i class="fa-regular fa-pen-to-square"></i>
              </button>
            </div>
            <div className="col-lg-2">
              <button
                className="delete"
                onClick={() => deleteCourse(item.courseid)}
              >
                <i class="fa-solid fa-trash"></i>
              </button>
            </div>
          </div>
        ))
      ) : (
        <Spinner />
      )}
      {showPopup ? (
        <CourseModal
          mode={mode}
          setShowPopup={setShowPopup}
          getCourses={getCourses}
        />
      ) : (
        ""
      )}
    </div>
  );
};

export default ListCourses;
