import React, { useEffect, useState } from 'react'
import ListStudents from '../admin/ListStudents'
import Spinner from '../global/Spinner';

const CourseStudents = ({setShowAnotherPopup,courseid}) => {

    const [students,setStudents] = useState(null);
    const [isDataFetched,setIsDataFetched] = useState(false);

    const getCourseStudents = async ()=>{
        try {
            const response = await fetch(`http://localhost:5000/courses/students/${courseid}`);
            const json = await response.json();
            console.log(json)
            setStudents(json);
            setIsDataFetched(false);
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getCourseStudents();
    }, [])
    

  return (
    <div className='overlay'>
        <div className="popup">
        <div className="row">
          <div className="col-lg-10">
            <h2 className="popup-title">Course Card</h2>
          </div>
          <div className="col-lg-2">
            <button
              className="close-button"
              onClick={() => setShowAnotherPopup(false)}
            >
              <i className="fa-sharp fa-solid fa-circle-xmark"></i>
            </button>
          </div>
            {isDataFetched ? <ListStudents students={students}/> : <Spinner />}
        </div>
        </div>
    </div>
  )
}

export default CourseStudents
